import React from 'react'
import { Text, Button, View } from 'react-native'
import Modal from '../components/Modal'

interface Props {
    title: string,
    message: string,
    shown: boolean,
    dismiss: () => void
}

export const MessageBoxModal = ({ title, message, shown, dismiss }: Props) => {

    return (
        <Modal visible={shown} onTouchOutside={() => dismiss && dismiss()}>
            <Text>{message}</Text>
            <View>
                <Button
                    title={title}
                    onPress={() => dismiss && dismiss()}
                />
            </View>
        </Modal>
    )
}
